    def load_data(self):       
        connection = sqlite3.connect("fci.db")
        results=connection.execute("SELECT LD_SET,CAPACITY_SET,OFF_POSITION_SET,FLAG_SET FROM GLOBAL_VAR  ") 
        for x in results:            
               self.comboBox_2.setCurrentText(str(x[0]))
               self.comboBox.setCurrentText(str(x[1]))
               self.comboBox_3.setCurrentText(str(x[2]))
               if(str(x[3]) == "Yes"):
                   self.radioButton.setEnabled(True)
                   self.radioButton_2.setDisabled(True)                   
               else:
                   self.radioButton_2.setEnabled(True)
                   self.radioButton.setDisabled(True)  
               
        connection.close()
        self.start_wt()
        
       
    
