    def pre_calibration_steps(self):
        self.label_41.show()
        self.pushButton_17.show()
        self.lineEdit.show()
        
        
    def start_calibration(self):
        if(self.lineEdit.text() != ""):
            
            connection = sqlite3.connect("fci.db")
            with connection:        
                    cursor = connection.cursor()
                    cursor.execute("UPDATE GLOBAL_VAR SET CALIBRATION_LOAD_SET='"+self.lineEdit.text()+"'")                    
            connection.commit();                    
            connection.close()
        
            try:
                self.ser = serial.Serial(
                                    port='/dev/ttyAMA0',
                                    baudrate=115200,
                                    bytesize=serial.EIGHTBITS,
                                    parity=serial.PARITY_NONE,
                                    stopbits=serial.STOPBITS_ONE,
                                    xonxoff=False,
                                    timeout = 0.05
                                )
            
                self.ser.flush()
                b = bytes('C', 'utf-8')
                self.ser.write(b)           
                time.sleep(1)
                self.pushButton_16.show()
                self.label_44.setText("Calibration is Done succefully, Please verify." )  
                self.label_44.show()
                connection = sqlite3.connect("fci.db")
                with connection:        
                    cursor = connection.cursor()
                    cursor.execute("INSERT INTO CALIBRATION_LOG(USER_NAME,USER_ID,LD_SET,CAPACITY_SET ,OFF_POSITON_SET ,FLAG_SET ,CALI_WT)  select LOGIN_USER_NAME,LOGIN_USER_ID,LD_SET,CAPACITY_SET,OFF_POSITION_SET,FLAG_SET,CALIBRATION_LOAD_SET from global_var")                    
                connection.commit();                    
                connection.close()
            except IOError:
                print("IO Errors")
                self.label_44.setText("IO Errors" )  
                self.label_44.show()
        else:
            self.label_44.setText("Please Enter the Load used." )  
            self.label_44.show()
            
    def verify(self):
        try:
            self.ser = serial.Serial(
                                port='/dev/ttyAMA0',
                                baudrate=115200,
                                bytesize=serial.EIGHTBITS,
                                parity=serial.PARITY_NONE,
                                stopbits=serial.STOPBITS_ONE,
                                xonxoff=False,
                                timeout = 0.05
                            )        
              
            self.line = self.ser.readline()
            time.sleep(1)
            #print("o/p:"+str(self.line))
            self.xstr0=str(self.line,'utf-8')
            self.xstr1=self.xstr0.replace("\r","")
            self.xstr2=self.xstr1.replace("\n","")
            self.buff=self.xstr2.split("_")
            if(len(self.buff) > 2):
                self.label_44.setText("Current Weight is : "+str(self.buff[0])+" kg." )  
                self.label_44.show()
            else:
                self.label_44.setText("OP:"+str(self.line))  
                self.label_44.show()
        except IOError:
            #print("IO Errors")
            self.label_44.setText("IO Errors" )  
            self.label_44.show()
            
            
            
    def start_wt(self):
        #print("Weight Started ....")
        try:
            self.ser = serial.Serial(
                                port='/dev/ttyUSB0',
                                baudrate=9600,
                                bytesize=serial.EIGHTBITS,
                                parity=serial.PARITY_NONE,
                                stopbits=serial.STOPBITS_ONE,
                                xonxoff=False,
                                timeout = 0.05
                            )
        
            self.ser.flush()       
            
            self.line = self.ser.readline(15)
            print("o/p:"+str(self.line))
             
            self.timer2.setInterval(5000)        
            self.timer2.timeout.connect(self.display_lcd_val)
            self.timer2.start(1)
            
            
        except IOError:
            print("IO Errors-load cell connections error")
            self.IO_error_flg=1
            self.label_44.setText("Load-Cell Connection Error.")
            self.label_44.show()
            
            
    def display_lcd_val(self):               
        #print(" inside display_lcd_val:"+str(self.IO_error_flg))
        if(self.IO_error_flg==0):
            try:
                self.line = self.ser.readline(20)
                print(" raw o/p:"+str(self.line)) 
                if (len(self.line) > 2):
                    
                    self.ser.flush()
                    self.ser.write(b'*D\r')
                    self.xstr3=str(self.line,'utf-8')
                    self.xstr2=self.xstr3[0:6]
                    #print("self.xstr3:"+str(self.xstr3)+" self.xstr2: "+str(self.xstr2))
                    try:
                         self.xstr4=int(self.xstr2)
                    except ValueError:                        
                        print("Value Error"+str(self.xstr2))
                        self.xstr4=0                    
                    try:
                        self.current_value=float(int(self.xstr4)/1000)
                    except ValueError:
                        print("Value Error :"+str(self.xstr4))
                        self.xstr4=0                                           
                    self.lcdNumber.setProperty("value", str(self.xstr4))                    
                    
            except IOError:
                print("IO Errors : Data Read Error") 
                self.IO_error_flg=1  
    
    
